mapper.delete:
DELETE bab_board
WHERE bab_no = #{bab_no}
  AND bab_id = #{bab_id}

DELETE sul_board
WHERE sul_no = #{sul_no}
  AND sul_id = #{sul_id}

DELETE nol_board
WHERE nol_no = #{nol_no}
  AND nol_id = #{nol_id}

mapper.update:
	UPDATE bab_board
  	   SET 
   	   bab_title = #{bab_title},
   	   bab_text = #{bab_text},
       bab_url = #{bab_url}
	 WHERE bab_id = #{bab_id}
   	   AND bab_no = #{bab_no}

	UPDATE sul_board
  	   SET 
   	   sul_title = #{sul_title},
   	   sul_text = #{sul_text},
       sul_url = #{sul_url}
	 WHERE sul_id = #{sul_id}
   	   AND sul_no = #{sul_no}

	UPDATE nol_board
  	   SET 
   	   nol_title = #{nol_title},
   	   nol_text = #{nol_text},
       nol_url = #{nol_url}
	 WHERE nol_id = #{nol_id}
   	   AND nol_no = #{nol_no}
