mapper.select:


SELECT
no,tel,addr,name,latitude,longitude,business,introduce,socre,hit,img,homepage,price
FROM store
WHERE map =

	
		AND protocol = #{protocol}
	
	
		AND price = #{price}

mapper.update:
	UPDATE store
       SET store_hit= store_hit +1
     WHERE store_no = #{store_no}

mapper.select:
SELECT rownum,store_score, store_no,store_img, nvl(store_price,'가격정보 없음')as "STORE_PRICE", store_name,store_addr,store_hit,store_business FROM (select * from store
WHERE REGEXP_LIKE(store_name,#{searchWord}) OR REGEXP_LIKE(store_keyword,#{searchWord})
ORDER BY store_hit desc)
AND store_business like '%'||#{store_business}||'%'
AND store_price = #{store_price}
AND store_business like '%'||#{store_business}||'%'
AND a.rno >= #{start}
AND a.rno <= #{end}

select re_date,re_writer,content
from reply
where re_no=#{re_no}
