mapper.update:
	UPDATE bab_board
  	   SET 
   	   bab_title = #{bab_title},
   	   bab_text = #{bab_text},
       bab_url = #{bab_url}
	 WHERE bab_no = #{bab_no}

	UPDATE sul_board
  	   SET 
   	   sul_title = #{sul_title},
   	   sul_text = #{sul_text},
       sul_url = #{sul_url}
	 WHERE sul_no = #{sul_no}

	UPDATE nol_board
  	   SET 
   	   nol_title = #{nol_title},
   	   nol_text = #{nol_text},
       nol_url = #{nol_url}
	 WHERE nol_no = #{nol_no}

UPDATE bab_board
   SET bab_hit = NVL(bab_hit, 0)+1
 WHERE bab_no = #{bab_no}

UPDATE sul_board
   SET sul_hit = NVL(sul_hit, 0)+1
 WHERE sul_no = #{sul_no}

UPDATE nol_board
   SET nol_hit = NVL(nol_hit, 0)+1
 WHERE nol_no = #{nol_no}
